mapper.select:
/* CommonCodeRepository.selectCommonCode 공통코드 조회 */
SELECT code, kor_name, eng_name
FROM PN_CODE
WHERE code_group1 = #{codeGroup1}
    AND code_group2 = #{codeGroup2}
    AND code = #{code}

/* CommonCodeRepository.selectCommonCodeList 공통코드 리스트 조회 */
SELECT code, kor_name, eng_name
FROM pn_code
WHERE code_group1 = #{codeGroup1}
  AND code_group2 = #{codeGroup2}
ORDER BY sort_seq, code
